import axios from 'axios';

// For development, use relative URLs (proxy will handle them)
// For production, use direct backend URL
const API_BASE_URL = process.env.NODE_ENV === 'development' ? '' : 'http://localhost:8080';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
  timeout: 10000, // 10 second timeout
});

// Add request interceptor for CORS proxy
api.interceptors.request.use(
  (config) => {
    // Add origin header for CORS proxy
    if (config.baseURL.includes('cors-anywhere')) {
      config.headers['Origin'] = 'http://localhost:3000';
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// Add response interceptor for better error handling
api.interceptors.response.use(
  (response) => response,
  (error) => {
    if (error.code === 'ERR_NETWORK') {
      console.error('Network Error - Please check if your backend is running on http://localhost:8080');
      console.error('If you have CORS issues, try using a different CORS proxy or configure CORS on your backend');
    }
    return Promise.reject(error);
  }
);

// Product API
export const productAPI = {
  getAll: () => api.get('/product'),
  getById: (id) => api.get(`/product/${id}`),
  create: (data) => api.post('/product', data),
  update: (id, data) => api.patch(`/product/${id}`, data),
  delete: (id) => api.delete(`/product/${id}`),
};

// Category API
export const categoryAPI = {
  getAll: () => api.get('/category'),
  getById: (id) => api.get(`/category/${id}`),
  create: (data) => api.post('/category', data),
  update: (id, data) => api.patch(`/category/${id}`, data),
  delete: (id) => api.delete(`/category/${id}`),
};

// Customer API
export const customerAPI = {
  getAll: () => api.get('/costumer'),
  getById: (id) => api.get(`/costumer/${id}`),
  create: (data) => api.post('/costumer', data),
  update: (id, data) => api.patch(`/costumer/${id}`, data),
  delete: (id) => api.delete(`/costumer/${id}`),
};

// Order API
export const orderAPI = {
  getAll: () => api.get('/order'),
  getById: (id) => api.get(`/order/${id}`),
  create: (data) => api.post('/order', data),
  update: (id, data) => api.patch(`/order/${id}`, data),
  delete: (id) => api.delete(`/order/${id}`),
  getByStatus: (status, params = {}) => api.get(`/order/filter/${status}`, { params }),
  getByCustomer: (id) => api.get(`/order/costumer/${id}`),
  getByBusiness: (id, params = {}) => api.get(`/order/business/${id}`, { params }),
};

// Payment API
export const paymentAPI = {
  getAll: () => api.get('/api/payment'),
  getById: (id) => api.get(`/api/payment/${id}`),
  create: (data) => api.post('/api/payment', data),
  update: (id, data) => api.patch(`/api/payment/${id}`, data),
  delete: () => api.delete('/api/payment'),
};

// Business API
export const businessAPI = {
  getAll: () => api.get('/api/business'),
  getById: (id) => api.get(`/api/business/${id}`),
  create: (data) => api.post('/api/business', data),
  update: (id, data) => api.patch(`/api/business/${id}`, data),
  delete: (id) => api.delete(`/api/business/${id}`),
};

export default api;
