import axios from 'axios';

const API_BASE_URL = 'https://cors-anywhere.herokuapp.com/http://localhost:8080';

const api = axios.create({
  baseURL: API_BASE_URL,
  headers: {
    'Content-Type': 'application/json',
  },
});

// Product API
export const productAPI = {
  getAll: () => api.get('/product'),
  getById: (id) => api.get(`/product/${id}`),
  create: (data) => api.post('/product', data),
  update: (id, data) => api.patch(`/product/${id}`, data),
  delete: (id) => api.delete(`/product/${id}`),
};

// Category API
export const categoryAPI = {
  getAll: () => api.get('/category'),
  getById: (id) => api.get(`/category/${id}`),
  create: (data) => api.post('/category', data),
  update: (id, data) => api.patch(`/category/${id}`, data),
  delete: (id) => api.delete(`/category/${id}`),
};

// Customer API
export const customerAPI = {
  getAll: () => api.get('/costumer'),
  getById: (id) => api.get(`/costumer/${id}`),
  create: (data) => api.post('/costumer', data),
  update: (id, data) => api.patch(`/costumer/${id}`, data),
  delete: (id) => api.delete(`/costumer/${id}`),
};

// Order API
export const orderAPI = {
  getAll: () => api.get('/order'),
  getById: (id) => api.get(`/order/${id}`),
  create: (data) => api.post('/order', data),
  update: (id, data) => api.patch(`/order/${id}`, data),
  delete: (id) => api.delete(`/order/${id}`),
  getByStatus: (status, params = {}) => api.get(`/order/filter/${status}`, { params }),
  getByCustomer: (id) => api.get(`/order/costumer/${id}`),
  getByBusiness: (id, params = {}) => api.get(`/order/business/${id}`, { params }),
};

// Payment API
export const paymentAPI = {
  getAll: () => api.get('/payment'),
  getById: (id) => api.get(`/payment/${id}`),
  create: (data) => api.post('/payment', data),
  update: (id, data) => api.patch(`/payment/${id}`, data),
  delete: () => api.delete('/payment'),
};

// Business API
export const businessAPI = {
  getAll: () => api.get('/business'),
  getById: (id) => api.get(`/business/${id}`),
  create: (data) => api.post('/business', data),
  update: (id, data) => api.patch(`/business/${id}`, data),
  delete: (id) => api.delete(`/business/${id}`),
};

export default api;
